import React, {useState} from 'react'
import { View, Text, StyleSheet, SafeAreaView, TextInput, Button } from 'react-native'
import axios from 'axios'

const AddArticle = (props) => {

    const data = props.route.params.article

    const [article, setArticle] = useState({
        name: data.title,
        price: data.price,
        picture: data.picture,
        content: data.content,
    })
    const articleId = toString(data.id)
    console.log(`http://localhost:8000/products/${articleId}`)




    const handelChange = (key, value) => {
        setArticle({ ...article, [key]: value })

    }

    const handleSubmit = async () => {
        try {
            await axios.put(`http://localhost:8000/products/${articleId}`, article)
            props.navigation.navigate('Home')
        } 
        catch {
            (error)=> (console.log(error))
        }

    }

    return (
        <SafeAreaView>
            {/* console.log({article}) */}
            <Text>Edit Article</Text>
            <TextInput
                placeholder={data.title}
                style={styles.input}
                onChangeText={(value) => handelChange("name", value)}
                // value={data.title}

            />
            <TextInput
                placeholder={data.price}
                style={styles.input}
                onChangeText={(value)=> handelChange("price", value)}
                keyboardType="numeric"
                value={data.price}
            />  
            <TextInput
                placeholder={data.picture}
                style={styles.input}
                onChangeText={(value)=> handelChange("picture", value)}
                // value={data.picture}
            />  
            <TextInput
                placeholder={data.contact}
                style={styles.input}
                onChangeText={(value)=> handelChange("content", value)}
                value={data.content}
                multiline
                numberOfLines={4}
                maxLength={40}
            />  
            <Button
                onPress={handleSubmit}
                title="Save"
                color="#841584"
                // accessibilityLabel="Learn more about this purple button"
            />


      </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});

export default AddArticle
