import React, {useState} from 'react'
import { View, Text, StyleSheet, SafeAreaView, TextInput, Button } from 'react-native'
import axios from 'axios'

const AddArticle = ({navigation}) => {


    const [article, setArticle] = useState({
        name: "",
        price: 0,
        picture: "",
        content: "",
    })

    const handelChange = (key, value) => {
        setArticle({...article, [key]: value})
    }

    const handleSubmit = async () => {
        try {
            await axios.post('http://localhost:8000/products/', article)
            navigation.push('HomeScreen')
        } 
        catch {
            (error)=> (console.log(error))
        }

    }

    return (
        <SafeAreaView>
            <TextInput
                placeholder='Article Name'
                style={styles.input}
                onChangeText={(value)=> handelChange("name", value)}
            />
            <TextInput
                placeholder='Article Price'
                style={styles.input}
                onChangeText={(value)=> handelChange("price", value)}
                keyboardType="numeric"
            />  
            <TextInput
                placeholder='Article Image'
                style={styles.input}
                onChangeText={(value)=> handelChange("picture", value)}
                // value={picture}
            />  
            <TextInput
                placeholder='Article Content'
                style={styles.input}
                onChangeText={(value)=> handelChange("content", value)}
                // value={content}
                multiline
                numberOfLines={4}
                maxLength={40}
            />  
            <Button
                onPress={handleSubmit}
                title="Save"
                color="#841584"
                // accessibilityLabel="Learn more about this purple button"
            />


      </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
});

export default AddArticle
